import React, { useState } from 'react'
import hackat from "../images/hackat.png"

function Certificate() {
      const[showImg, setShowImg] = useState("hidden")
      return (
            <div className="img-preview" style={{visibility: showImg}}>
                  {!showImg ? <div>
                  <img src={hackat} alt= "certificate" />
                  <button onClick={() => setShowImg("visible")}></button>
            </div> : setShowImg(false) }
            </div>


      )
}
export default Certificate
