import React, { useState } from 'react';
import hackat from '../images/hackat.png';

function Certificate() {
	const[showImage, setShowImg] = useState("visible")
	return (
		<div id="certify" className="img-certify row">
			<div col-12>
				<img className="img-preview " src={hackat} alt="certificate" style={{visibility: showImage}}/>
				<button onClick={() => setShowImg('hidden')} />
			</div>
		</div>
	);
}
export default Certificate;
